import React, { Component } from "react";
import { Row, Col } from "reactstrap";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addToCart } from "../actions/cartActions";

class CartPage extends Component {
  render() {
    console.log(this.props.cart);

    const pricePerItem = this.props.cart.map(
      p =>
        (p.quantity *
          parseFloat(p.item.price.slice(1, p.item.price.length)) *
          100) /
        100
    );
    console.log("pp", pricePerItem);
    const total = pricePerItem
      ? pricePerItem.reduce((prev, curr) => prev + curr)
      : "";
    // const totalQuantity = this.props.cart
    //   ? this.props.cart.map(p => p.quantity.reduce((prev, cur) => prev + cur))
    //   : "";

    const cartItem = this.props.cart.map((t, i) => (
      <Row key={i}>
        <Col>
          <h3>{t.item.title}</h3>
        </Col>
        <Col>{t.item.price}</Col>
        <Col>{t.quantity}</Col>
        <Col>{pricePerItem}</Col>
      </Row>
    ));

    return (
      <div>
        <h1>Your basket :</h1>
        {cartItem}
        <Row>
          <Col>Total</Col>
          {/* <Col>{totalQuantity}</Col> */}
          <Col>{total}</Col>
        </Row>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  cart: state.cart
});

const mapDispatchToProps = {
  CartPage,
  addToCart
};

CartPage.propTypes = {
  cart: PropTypes.array.isRequired
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CartPage);
