import { ADD_TO_CART } from "../actions/types";

export default function(state = [], action) {
  switch (action.type) {
    case ADD_TO_CART:
      console.log("r", ...state.map(t => t.title));
      console.log(action.item.title);
      const index = state.findIndex(el => el.title === action.item.title);
      console.log(index);
      return [...state, action.item];

    default:
      return state;
  }
}
