import { ADD_TO_CART, INCREASE_QUANTITY } from "../actions/types";

export default function(state = [], action) {
  switch (action.type) {
    case ADD_TO_CART:
      if (state.findIndex(el => el.item.title === action.item.title) === -1) {
        return [...state, { item: action.item, quantity: action.quantity + 1 }];
      } else {
        console.log(state.map(s => s.item));
        return [...state, { item: state.item, quantity: state.quantity + 1 }];
      }
    default:
      return state;
  }
}
