import { ADD_TO_CART, INCREASE_QUANTITY } from "../actions/types";

export default function(state = [], action) {
  switch (action.type) {
    case ADD_TO_CART:
      console.log(state);
      const index = state.findIndex(
        el => console.log("el"),
        console.log("ac", action.item)
      );
      console.log(index);
    // if (index === -1) return [...state, { item: action.item }];
    // case INCREASE_QUANTITY:
    //   return [...state, { quantity: action.quantity + 1 }];
    default:
      return state;
  }
}
