import { ADD_TO_CART } from "../actions/types";

export default function(state = [], action) {
  switch (action.type) {
    case ADD_TO_CART:
      let index = state.findIndex(
        el => el.map(e => e.title) === action.items.map(i => i.title)
      );
      console.log(...state.map(x => x.map(y => y.title)));
      console.log(...action.items.map(x => x.title));
      if (index === -1) return [...state, action.items];
      break;
    default:
      return state;
  }
}
