import { ADD_TO_CART } from "./types";
import axios from "axios";

export const addToCart = id => dispatch => {
  axios
    .get(`https://api.itbook.store/1.0/search/${id}`)
    .then(res => console.log("e", res.data.books))
    .then(
      items => console.log(items.map(i => i)),
      dispatch({
        type: ADD_TO_CART
      })
    );
};
