import { ADD_TO_CART } from "../actions/types";

export default function(state = [], action) {
  switch (action.type) {
    case ADD_TO_CART:
      const index = state.findIndex(el => el.item.title === action.item.title);
      if (index === -1)
        return [...state, { item: action.item, quantity: action.quantity++ }];

    default:
      return state;
  }
}
