import React, { Component } from "react";
import { Row, Col } from "reactstrap";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addToCart } from "../actions/cartActions";

class CartPage extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    console.log("t", this.props.cart.map(x => x.items.map(y => y.title)));
    const cartItem = this.props.cart.map((c, i) => (
      <Row key={i}>
        <Col>
          <h3>{c.title}</h3>
        </Col>
        <Col>{c.price}</Col>
      </Row>
    ));
    return (
      <div>
        <h1>Your basket :</h1>
        {cartItem}
      </div>
    );
  }
}
const mapStateToProps = state => ({
  cart: state.cart
});

const mapDispatchToProps = {
  CartPage,
  addToCart
};

CartPage.propTypes = {
  cart: PropTypes.array.isRequired
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(CartPage);
