import { ADD_TO_CART } from "../actions/types";

export default function(state = [], action) {
  switch (action.type) {
    case ADD_TO_CART:
      let index = state.findIndex(el => console.log("r", el));
      console.log(action.items);
      if (index === -1) {
        return [...state, action.items];
      }
      break;
    default:
      return state;
  }
}
