import { ADD_TO_CART, INCREASE_QUANTITY } from "../actions/types";

export default function(state = [], action) {
  switch (action.type) {
    case ADD_TO_CART:
      // const index = state.findIndex(el => el.item.title === action.item.title);
      // console.log(index);
      if (state.findIndex(el => el.item.title === action.item.title) === -1) {
        state.cart.reduce((prev, curr) => console.log(prev, curr));
      }
    //  return [...state, { item: action.item }];
    // case INCREASE_QUANTITY:
    //   return [...state, { quantity: action.quantity + 1 }];
    default:
      return state;
  }
}
