import { ADD_TO_CART } from "../actions/types";

export default function(state = [], action) {
  switch (action.type) {
    case ADD_TO_CART:
      console.log("red", state.item);
      return {
        ...state,
        item: [...state, action.item]
      };

    default:
      return state;
  }
}
